import React, { useState } from "react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Card } from "./ui/card";
import { Alert, AlertDescription } from "./ui/alert";
import { Loader2, User, ArrowLeft, Mail } from "lucide-react";
import krishiBandhuLogo from "figma:asset/93a11ef0f4c1a2af6f65d747ec6e1d56f7092a96.png";

interface AuthFormProps {
  onAuthSuccess: (accessToken: string) => void;
}

export function AuthForm({ onAuthSuccess }: AuthFormProps) {
  const [isLogin, setIsLogin] = useState(true);
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [resetEmail, setResetEmail] = useState("");
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
    farmSize: "",
    location: "",
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      if (isLogin) {
        // Login logic using Supabase
        const { supabase } = await import(
          "../utils/supabase/client"
        );

        const { data, error } =
          await supabase.auth.signInWithPassword({
            email: formData.email,
            password: formData.password,
          });

        if (error) {
          setError(`Login failed: ${error.message}`);
          return;
        }

        if (data.session?.access_token) {
          onAuthSuccess(data.session.access_token);
        }
      } else {
        // Register via our server
        const { projectId, publicAnonKey } = await import(
          "../utils/supabase/info"
        );

        const response = await fetch(
          `https://${projectId}.supabase.co/functions/v1/make-server-1aa6be21/signup`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${publicAnonKey}`,
            },
            body: JSON.stringify(formData),
          },
        );

        const result = await response.json();

        if (!response.ok) {
          setError(`Registration failed: ${result.error}`);
          return;
        }

        // After successful registration, log them in
        const { supabase } = await import(
          "../utils/supabase/client"
        );

        const { data, error } =
          await supabase.auth.signInWithPassword({
            email: formData.email,
            password: formData.password,
          });

        if (error) {
          setError(
            `Auto-login after registration failed: ${error.message}`,
          );
          return;
        }

        if (data.session?.access_token) {
          onAuthSuccess(data.session.access_token);
        }
      }
    } catch (error) {
      console.error("Auth error:", error);
      setError(
        `Authentication error: ${error instanceof Error ? error.message : "Unknown error"}`,
      );
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordReset = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccessMessage("");

    try {
      const { supabase } = await import("../utils/supabase/client");

      const { error } = await supabase.auth.resetPasswordForEmail(resetEmail, {
        redirectTo: `${window.location.origin}/reset-password`,
      });

      if (error) {
        setError(`Password reset failed: ${error.message}`);
        return;
      }

      setSuccessMessage(
        "Password reset email sent! Please check your inbox and follow the instructions to reset your password."
      );
    } catch (error) {
      console.error("Password reset error:", error);
      setError(
        `Password reset error: ${error instanceof Error ? error.message : "Unknown error"}`
      );
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 flex items-center justify-center p-4">
      <Card className="w-full max-w-md p-6 space-y-6">
        {/* Logo and header */}
        <div className="text-center space-y-2">
          <img
            src={krishiBandhuLogo}
            alt="KrishiBandhu Logo"
            className="h-20 w-auto mx-auto"
          />
          <h1 className="text-2xl font-semibold text-green-800">
            KrishiBandhu
          </h1>
          <p className="text-sm text-green-600">
            Smart Crop Advisory System
          </p>
        </div>

        {/* Conditional form rendering */}
        {isForgotPassword ? (
          <form onSubmit={handlePasswordReset} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="resetEmail">Email Address</Label>
              <Input
                id="resetEmail"
                type="email"
                value={resetEmail}
                onChange={(e) => setResetEmail(e.target.value)}
                placeholder="Enter your email address"
                required
              />
            </div>

            {(error || successMessage) && (
              <Alert className={successMessage ? "border-green-200 bg-green-50" : "border-red-200 bg-red-50"}>
                <AlertDescription className={successMessage ? "text-green-800" : "text-red-800"}>
                  {successMessage || error}
                </AlertDescription>
              </Alert>
            )}

            <Button
              type="submit"
              className="w-full bg-green-600 hover:bg-green-700"
              disabled={loading}
            >
              {loading ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Sending reset email...
                </>
              ) : (
                <>
                  <Mail className="h-4 w-4 mr-2" />
                  Send Reset Email
                </>
              )}
            </Button>

            <Button
              type="button"
              variant="ghost"
              onClick={() => {
                setIsForgotPassword(false);
                setError("");
                setSuccessMessage("");
                setResetEmail("");
              }}
              className="w-full text-green-600 hover:text-green-700"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Sign In
            </Button>
          </form>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) =>
                handleInputChange("email", e.target.value)
              }
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <Input
              id="password"
              type="password"
              value={formData.password}
              onChange={(e) =>
                handleInputChange("password", e.target.value)
              }
              placeholder="Enter your password"
              required
            />
          </div>

          {!isLogin && (
            <>
              <div className="space-y-2">
                <Label htmlFor="name">Full Name</Label>
                <Input
                  id="name"
                  type="text"
                  value={formData.name}
                  onChange={(e) =>
                    handleInputChange("name", e.target.value)
                  }
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="farmSize">
                  Farm Size (acres)
                </Label>
                <Input
                  id="farmSize"
                  type="text"
                  value={formData.farmSize}
                  onChange={(e) =>
                    handleInputChange(
                      "farmSize",
                      e.target.value,
                    )
                  }
                  placeholder="e.g., 5.2 acres"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="location">Location</Label>
                <Input
                  id="location"
                  type="text"
                  value={formData.location}
                  onChange={(e) =>
                    handleInputChange(
                      "location",
                      e.target.value,
                    )
                  }
                  placeholder="Village, District, State"
                  required
                />
              </div>
            </>
          )}

          {error && (
            <Alert className="border-red-200 bg-red-50">
              <AlertDescription className="text-red-800">
                {error}
              </AlertDescription>
            </Alert>
          )}

          {/* Forgot Password Link - Only show in login mode */}
          {isLogin && (
            <div className="text-right">
              <Button
                type="button"
                variant="ghost"
                onClick={() => {
                  setIsForgotPassword(true);
                  setError("");
                  setSuccessMessage("");
                }}
                className="text-sm text-green-600 hover:text-green-700 p-0 h-auto font-normal"
              >
                Forgot Password?
              </Button>
            </div>
          )}

          <Button
            type="submit"
            className="w-full bg-green-600 hover:bg-green-700"
            disabled={loading}
          >
            {loading ? (
              <>
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                {isLogin
                  ? "Signing in..."
                  : "Creating account..."}
              </>
            ) : (
              <>
                <User className="h-4 w-4 mr-2" />
                {isLogin ? "Sign In" : "Create Account"}
              </>
            )}
          </Button>
        </form>
        )}

        {/* Toggle between login/register - Hide when in forgot password mode */}
        {!isForgotPassword && (
          <div className="text-center">
            <Button
              variant="ghost"
              onClick={() => {
                setIsLogin(!isLogin);
                setError("");
                setSuccessMessage("");
              }}
              className="text-green-600 hover:text-green-700"
            >
              {isLogin
                ? "Don't have an account? Sign up"
                : "Already have an account? Sign in"}
            </Button>
          </div>
        )}
      </Card>
    </div>
  );
}

src/components/ConsultationModal.tsx.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Textarea } from './ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Alert, AlertDescription } from './ui/alert';
import { Loader2, MessageCircle, CheckCircle } from 'lucide-react';

interface ConsultationModalProps {
  isOpen: boolean;
  onClose: () => void;
  accessToken: string;
}

export function ConsultationModal({ isOpen, onClose, accessToken }: ConsultationModalProps) {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    question: '',
    category: '',
    urgency: ''
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const { projectId } = await import('../utils/supabase/info');

      const response = await fetch(`https://${projectId}.supabase.co/functions/v1/make-server-1aa6be21/consultation`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`,
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok) {
        setSuccess(true);
        setFormData({ question: '', category: '', urgency: '' });
      } else {
        setError(`Failed to submit consultation: ${result.error}`);
      }
    } catch (error) {
      console.error('Consultation submission error:', error);
      setError('Failed to submit consultation request. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setSuccess(false);
    setError('');
    setFormData({ question: '', category: '', urgency: '' });
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <MessageCircle className="h-5 w-5 text-orange-600" />
            <span>Ask an Expert</span>
          </DialogTitle>
        </DialogHeader>

        {success ? (
          <div className="space-y-4 text-center">
            <CheckCircle className="h-12 w-12 text-green-600 mx-auto" />
            <div>
              <h3 className="font-medium text-gray-900 mb-2">Request Submitted!</h3>
              <p className="text-sm text-gray-600">
                Your consultation request has been submitted. An expert will respond within 24-48 hours.
              </p>
            </div>
            <Button onClick={handleClose} className="w-full">
              Done
            </Button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="category">Category</Label>
              <Select 
                value={formData.category} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="crop-selection">Crop Selection</SelectItem>
                  <SelectItem value="pest-disease">Pest & Disease</SelectItem>
                  <SelectItem value="fertilizer">Fertilizer & Nutrition</SelectItem>
                  <SelectItem value="irrigation">Irrigation</SelectItem>
                  <SelectItem value="market">Market & Pricing</SelectItem>
                  <SelectItem value="weather">Weather Related</SelectItem>
                  <SelectItem value="other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="urgency">Urgency</Label>
              <Select 
                value={formData.urgency} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, urgency: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select urgency" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low - General inquiry</SelectItem>
                  <SelectItem value="medium">Medium - Need answer soon</SelectItem>
                  <SelectItem value="high">High - Urgent problem</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="question">Your Question</Label>
              <Textarea
                id="question"
                value={formData.question}
                onChange={(e) => setFormData(prev => ({ ...prev, question: e.target.value }))}
                placeholder="Describe your farming question or problem in detail..."
                className="min-h-[100px]"
                required
              />
            </div>

            {error && (
              <Alert className="border-red-200 bg-red-50">
                <AlertDescription className="text-red-800">
                  {error}
                </AlertDescription>
              </Alert>
            )}

            <div className="flex space-x-3">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleClose}
                className="flex-1"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="flex-1 bg-orange-600 hover:bg-orange-700"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Submitting...
                  </>
                ) : (
                  'Submit Question'
                )}
              </Button>
            </div>
          </form>
        )}
      </DialogContent>
    </Dialog>
  );
}

